CREATE TABLE
    roles (
        id INT PRIMARY KEY NOT NULL AUTO_INCREMENT,
        role_name VARCHAR(254) NOT NULL
    );

CREATE TABLE
    users (
        id INT PRIMARY KEY NOT NULL AUTO_INCREMENT,
        firstname VARCHAR(100) NOT NULL,
        lastname VARCHAR(100) NOT NULL,
        photo VARCHAR(254),
        email VARCHAR(100) NOT NULL UNIQUE,
        is_active TINYINT DEFAULT 1,
        password VARCHAR(254) NOT NULL,
        creation_date DATE DEFAULT (CURRENT_DATE)
    );

CREATE TABLE
    users_roles (
        id INT AUTO_INCREMENT NOT NULL PRIMARY KEY,
        user_id INT,
        role_id INT,
        FOREIGN KEY (user_id) REFERENCES users(id),
        FOREIGN KEY (role_id) REFERENCES roles(id)
    );

CREATE TABLE
    `status` (
        id INT PRIMARY KEY NOT NULL AUTO_INCREMENT,
        title VARCHAR(100) NOT NULL
    );

CREATE TABLE
    concernedhub (
        id INT PRIMARY KEY NOT NULL AUTO_INCREMENT,
        title VARCHAR(100) NOT NULL
    );

CREATE TABLE
    decisions (
        id INT PRIMARY KEY NOT NULL AUTO_INCREMENT,
        title VARCHAR(254) NOT NULL,
        content TEXT NOT NULL,
        usefulness TEXT NOT NULL,
        context TEXT NOT NULL,
        benefit TEXT NOT NULL,
        disadvantages TEXT NOT NULL,
        first_decision_content TEXT,
        concerned_hub_id INT NOT NULL,
        initial_date DATE DEFAULT (CURRENT_DATE) NOT NULL,
        deadline_comment DATE,
        first_take_decision DATE,
        deadline_conflict DATE,
        final_take_decision DATE,
        positives_votes INT,
        negatives_votes INT,
        is_validated TINYINT,
        status_id INT NOT NULL,
        FOREIGN KEY (status_id) REFERENCES `status`(id) ON DELETE CASCADE
    );

CREATE TABLE
    users_decisions (
        id INT PRIMARY KEY NOT NULL AUTO_INCREMENT,
        user_id INT NOT NULL,
        decision_id INT NOT NULL,
        FOREIGN KEY (user_id) REFERENCES users(id),
        FOREIGN KEY (decision_id) REFERENCES decisions(id) ON DELETE CASCADE
    );

CREATE TABLE
    tagged_as_experts (
        id INT PRIMARY KEY NOT NULL AUTO_INCREMENT,
        user_id INT NOT NULL,
        decision_id INT NOT NULL,
        is_notif_read TINYINT DEFAULT 1,
        FOREIGN KEY (user_id) REFERENCES users(id),
        FOREIGN KEY (decision_id) REFERENCES decisions(id) ON DELETE CASCADE
    );

CREATE TABLE
    tagged_as_impacted (
        id INT PRIMARY KEY NOT NULL AUTO_INCREMENT,
        user_id INT NOT NULL,
        decision_id INT NOT NULL,
        is_notif_read TINYINT DEFAULT 1,
        FOREIGN KEY (user_id) REFERENCES users(id),
        FOREIGN KEY (decision_id) REFERENCES decisions(id) ON DELETE CASCADE
    );

CREATE TABLE
    comments (
        id INT PRIMARY KEY NOT NULL AUTO_INCREMENT,
        user_id INT NOT NULL,
        decision_id INT NOT NULL,
        creation_date DATE DEFAULT (CURRENT_DATE) NOT NULL,
        FOREIGN KEY (user_id) REFERENCES users(id),
        FOREIGN KEY (decision_id) REFERENCES decisions(id),
        `comment` TEXT NOT NULL,
        vote TINYINT
    );

