SET GLOBAL event_scheduler = ON;

CREATE EVENT UPDATE_STATUS_EVENT 
	ON SCHEDULE EVERY 1 DAY STARTS CURRENT_TIMESTAMP
	DO
	UPDATE decisions
	SET status_id = 2
	WHERE
	    status_id = 1
	    AND DATE(now() - INTERVAL 15 DAY) >= initial_date;
	UPDATE decisions
	SET status_id = 7
	WHERE
	    status_id = 2
	    AND DATE(now() - INTERVAL 7 DAY) >= deadline_comment;
	UPDATE decisions
	SET status_id = 4
	WHERE
	    status_id = 3
	    AND DATE(now() - INTERVAL 15 DAY) >= first_take_decision;
	UPDATE decisions
	SET
	    status_id = 5,
	    is_validated = 0
	WHERE
	    status_id = 4
	    AND DATE(now() - INTERVAL 15 DAY) >= deadline_conflict;
	UPDATE decisions
	SET status_id = 6
	WHERE
	    status_id = 5
	    AND DATE(now() - INTERVAL 90 DAY) >= final_take_decision;
